mapper.select:
select subject, content
from noticeBoard
where noticeSeq = #{noticeSeq}

select rno, userid, name, birthDate, email, phone, registerDate, status from ( select rownum as rno, userid, name, birthDate, email, phone, registerDate, status from ( select userid, name, to_char(birthDate,'yyyy-mm-dd') AS birthDate, email, phone, to_char(registerDate,'yyyy-mm-dd') AS registerDate, status from member where status in (1,0)
and ${searchType} like '%' || #{searchWord} || '%'
) V )T where rno between #{startRno} and #{endRno}
