mapper.select:
select rno, noticeSeq, subject, content, regDate from ( select rownum AS rno, noticeSeq, subject, content, regDate from ( select noticeSeq, subject, content, to_char(regDate, 'yyyy-mm-dd') as regDate from noticeboard
where content LIKE '%'||#{searchWord}||'%'
order by noticeSeq desc ) V )T where rno between #{startRno} and #{endRno}

select V.rno, V.infoSeq, V.subject, V.content, V.imgName, V.regDate from (select rownum as rno, infoSeq, subject, content, imgName, to_char(regDate, 'yyyy-mm-dd') as regDate from infoboard
where content LIKE '%'||#{searchWord}||'%'
) V where V.rno between #{startRno} and #{endRno} order by V.regDate

select preseq, presubject, noticeseq, subject, content, regDate, nextseq, nextsubject from ( select lag(noticeseq, 1) over(order by noticeseq desc) as preseq , lag(subject, 1) over(order by noticeseq desc) as presubject
, noticeseq, subject, content, to_char(regDate, 'yyyy-mm-dd') as regDate
, lead(noticeseq, 1) over(order by noticeseq desc) as nextseq , lead(subject, 1) over(order by noticeseq desc) as nextsubject from noticeBoard ) V where V.noticeSeq = #{noticeSeq}

select preseq, presubject, infoSeq, subject, content, regDate, nextseq, nextsubject from ( select lag(infoSeq, 1) over(order by infoSeq desc) as preseq , lag(subject, 1) over(order by infoSeq desc) as presubject
, infoSeq, subject, content, to_char(regDate, 'yyyy-mm-dd') as regDate
, lead(infoSeq, 1) over(order by infoSeq desc) as nextseq , lead(subject, 1) over(order by infoSeq desc) as nextsubject from infoBoard ) V where V.infoSeq = #{infoSeq}
