mapper.select:
select hpseq, hpname, address, phone, dept, info, mainimg, subimg1, subimg2
from hospitalinfo
where hpseq=#{hpSeq}

select trunc(avg(rating)) as avg 
from hospitalreview 
where hpseq=#{hpseq}

select count(*)
from bookmark
where userid = #{loginuserId} and hpseq = #{hpSeq}


		select userid, content, regDate, rating 
     	from hospitalreview
     	where hpseq=#{hpSeq} and userid = #{loginuserId}


		select userid, content, regdate, rating 
     		from
	     	(select row_number() over (order by regDate desc) as rno,
	     		userid, content, regDate, rating 
	     	from hospitalreview
	     	where hpseq=#{hpSeq}
	     	)V
     	where V.rno between #{startRno} and #{endRno}

select count(*)
from hospitalreview
where hpseq = #{hpSeq}

select pharmseq, name, address, phone, img,
from pharmacyinfo
where pharmseq=#{phSeq}

select trunc(avg(rating)) as avg 
from pharmacyreview 
where pharmseq=#{phSeq}


		select userid, content, regDate, rating 
     	from pharmacyreview
     	where pharmseq=#{phSeq} and userid = #{loginuserId}


		select userid, content, regdate, rating 
     		from
	     	(select row_number() over (order by regDate desc) as rno,
	     		userid, content, regDate, rating 
	     	from pharmacyreview
	     	where pharmseq=#{phSeq}
	     	)V
     	where V.rno between #{startRno} and #{endRno}

select count(*)
from pharmacyreview
where pharmseq = #{phSeq}
